MERGE INTO [dbo].[LeadStages] AS Target 
            USING ( VALUES
        
		        (NEWID(), 'Lead Created', '#787878', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'SCO Generated', '#1fd5e0', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'SCO Accepted', '#7997fe', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'SCO Rejected', '#ebfe72', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Closed', '#65bb63', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Invoiced', '#fec074', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Lost', '#d97373', @Currentdate, @UserId,@CompanyId)
            )
            AS Source ([Id], [Name], [Color], [CreatedDateTime], [CreatedByUserId],[CompanyId])
            ON Target.[Name] = Source.[Name]  AND Target.CompanyId=Source.CompanyId
            WHEN MATCHED THEN 
            UPDATE SET [Id] = Source.[Id],
                       [Name] = Source.[Name],
                       [Color] = Source.[Color],
                       [CompanyId] = Source.[CompanyId],
                       [CreatedDateTime] = Source.[CreatedDateTime],
                       [CreatedByUserId] = Source.[CreatedByUserId]
            WHEN NOT MATCHED BY TARGET THEN 
            INSERT ([Id], [Name], [Color], [CreatedDateTime], [CreatedByUserId], [CompanyId]) 
            VALUES ([Id], [Name], [Color], [CreatedDateTime], [CreatedByUserId], [CompanyId]);

