CREATE PROCEDURE [dbo].[Marker377]
(
    @CompanyId UNIQUEIDENTIFIER,
    @UserId UNIQUEIDENTIFIER,
    @RoleId UNIQUEIDENTIFIER
)
AS
BEGIN
    
    DECLARE @Currentdate DATETIME = GETDATE()

    MERGE INTO [dbo].[RFQStatus] AS Target 
    USING ( VALUES
		(NEWID(), 'Open', 'Open', '#0cf27b', @Currentdate, @UserId,@CompanyId)
		--,(NEWID(), 'Inprogress', 'Inprogress', '#deff28', @Currentdate, @UserId,@CompanyId)
		,(NEWID(), 'Approved', 'Approved', '#4cc1bb', @Currentdate, @UserId,@CompanyId)
		,(NEWID(), 'Rejected', 'Rejected', '#FF6363', @Currentdate, @UserId,@CompanyId)
    )
    AS Source ([Id], [StatusName], [RFQStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId],[CompanyId])
    ON Target.[RFQStatusName] = Source.[RFQStatusName]  AND Target.CompanyId=Source.CompanyId
    WHEN MATCHED THEN 
    UPDATE SET [StatusName] = Source.[StatusName],
                [StatusColor] = Source.[StatusColor]
    WHEN NOT MATCHED BY TARGET THEN 
    INSERT ([Id], [StatusName],[RFQStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]) 
    VALUES ([Id], [StatusName],[RFQStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]);


    MERGE INTO [dbo].[Widget] AS Target 
    USING ( VALUES 
     (NEWID(),'This app can be used to update the name and color of a contract status. These contract status are used in purchased, sales and vessel contracts and its process.', N'Contract status', CAST(N'2021-10-07 19:27:24.523' AS DateTime),@UserId,@CompanyId,NULL,NULL,NULL)
    )
    AS Source ([Id],[Description], [WidgetName], [CreatedDateTime],[CreatedByUserId],[CompanyId],[UpdatedDateTime],[UpdatedByUserId],[InActiveDateTime]) 
    ON Target.[WidgetName] = Source.[WidgetName] AND Target.[CompanyId] = Source.[CompanyId]
    WHEN MATCHED THEN 
    UPDATE SET [WidgetName] = Source.[WidgetName],
               [CreatedDateTime] = Source.[CreatedDateTime],
               [CreatedByUserId] = Source.[CreatedByUserId],
               [CompanyId] =  Source.[CompanyId],
               [Description] =  Source.[Description],
               [UpdatedDateTime] =  Source.[UpdatedDateTime],
               [UpdatedByUserId] =  Source.[UpdatedByUserId],
               [InActiveDateTime] =  Source.[InActiveDateTime]
    WHEN NOT MATCHED BY TARGET THEN 
    INSERT([Id],[Description], [WidgetName], [CreatedDateTime],[CreatedByUserId],[CompanyId],[UpdatedDateTime],[UpdatedByUserId],[InActiveDateTime]) 
    VALUES([Id],[Description], [WidgetName], [CreatedDateTime],[CreatedByUserId],[CompanyId],[UpdatedDateTime],[UpdatedByUserId],[InActiveDateTime]);
    
    MERGE INTO [dbo].[WidgetModuleConfiguration] AS Target 
        USING (VALUES 
    (NEWID(),(SELECT Id FROM Widget WHERE WidgetName = 'RFQ status' AND CompanyId = @CompanyId),'05E222F2-6EA3-4CA6-8788-52416E67475F',@UserId,@Currentdate)
     )
    AS Source ([Id], [WidgetId], [ModuleId], [CreatedByUserId],[CreatedDateTime])
    ON Target.[WidgetId] = Source.[WidgetId]  AND Target.[ModuleId] = Source.[ModuleId]  
    WHEN MATCHED THEN 
    UPDATE SET
               [WidgetId] = Source.[WidgetId],
               [ModuleId] = Source.[ModuleId],
               [CreatedDateTime] = Source.[CreatedDateTime],
               [CreatedByUserId] = Source.[CreatedByUserId]
    WHEN NOT MATCHED BY TARGET AND Source.[WidgetId] IS NOT NULL AND Source.[ModuleId] IS NOT NULL THEN 
    INSERT ([Id], [WidgetId], [ModuleId], [CreatedByUserId],[CreatedDateTime]) VALUES ([Id], [WidgetId], [ModuleId], [CreatedByUserId],[CreatedDateTime]);   
    
    MERGE INTO [dbo].[WidgetRoleConfiguration] AS Target 
    USING ( VALUES
        (NEWID(),(SELECT Id FROM Widget WHERE CompanyId = @CompanyId AND WidgetName = 'RFQ status'),@RoleId,GETDATE(),@UserId)
    )
    AS Source (Id,WidgetId,RoleId,CreatedDateTime,CreatedByUserId)
    ON Target.WidgetId = Source.WidgetId AND Target.RoleId = Source.RoleId  
    WHEN MATCHED THEN 
    UPDATE SET [RoleId] = Source.[RoleId],
                WidgetId = Source.WidgetId,
                [CreatedDateTime] = Source.[CreatedDateTime],
                [CreatedByUserId] = Source.[CreatedByUserId]
    WHEN NOT MATCHED BY TARGET THEN 
    INSERT ([Id], WidgetId, RoleId, [CreatedDateTime], [CreatedByUserId]) 
    VALUES ([Id], WidgetId, RoleId, [CreatedDateTime], [CreatedByUserId]);
    
    MERGE INTO [dbo].[PaymentCondition] AS Target
    USING ( VALUES
        (NEWID(), 'Before breaking bulk', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'Immediately after loading completion', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'Before vessel reaches discharge port', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'BBB or immediately after loading completion', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'Others', @Currentdate, @UserId,@CompanyId)
    )
    AS Source ([Id], [PaymentConditionName], [CreatedDateTime], [CreatedByUserId],[CompanyId])
    ON Target.[PaymentConditionName] = Source.[PaymentConditionName] AND Target.CompanyId=Source.CompanyId
    WHEN MATCHED THEN
    UPDATE SET [Id] = Source.[Id],
    [PaymentConditionName] = Source.[PaymentConditionName],
    [CompanyId] = Source.[CompanyId],
    [CreatedDateTime] = Source.[CreatedDateTime],
    [CreatedByUserId] = Source.[CreatedByUserId]
    WHEN NOT MATCHED BY TARGET THEN
    INSERT ([Id], [PaymentConditionName], [CreatedDateTime], [CreatedByUserId], [CompanyId])
    VALUES ([Id], [PaymentConditionName], [CreatedDateTime], [CreatedByUserId], [CompanyId]);        
        
    MERGE INTO [dbo].[Tolerance] AS Target
    USING ( VALUES
        (NEWID(), 'plus/minus 2%', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'plus/minus 3%', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'plus/minus 4%', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'plus/minus 5%', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'plus/minus 8%', @Currentdate, @UserId,@CompanyId)
        ,(NEWID(), 'Others', @Currentdate, @UserId,@CompanyId)
    )
    AS Source ([Id], [ToleranceName], [CreatedDateTime], [CreatedByUserId],[CompanyId])
    ON Target.[ToleranceName] = Source.[ToleranceName] AND Target.CompanyId=Source.CompanyId
    WHEN MATCHED THEN
    UPDATE SET [Id] = Source.[Id],
    [ToleranceName] = Source.[ToleranceName],
    [CompanyId] = Source.[CompanyId],
    [CreatedDateTime] = Source.[CreatedDateTime],
    [CreatedByUserId] = Source.[CreatedByUserId]
    WHEN NOT MATCHED BY TARGET THEN
    INSERT ([Id], [ToleranceName], [CreatedDateTime], [CreatedByUserId], [CompanyId])
    VALUES ([Id], [ToleranceName], [CreatedDateTime], [CreatedByUserId], [CompanyId]);

