CREATE PROCEDURE [dbo].[Marker379]
(
    @CompanyId UNIQUEIDENTIFIER,
    @UserId UNIQUEIDENTIFIER,
    @RoleId UNIQUEIDENTIFIER
)
AS
BEGIN
DECLARE @Currentdate DATETIME=GETDATE();
 MERGE INTO [dbo].[ClientInvoiceStatus] AS Target 
            USING ( VALUES
		        (NEWID(), 'Created', 'Created', '#00B4D8', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Invoice Shared', 'Shared', '#2666CF', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Invoice Accepted', 'Invoice Accepted', '#00D1FF', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Invoice Rejected', 'Invoice Rejected', '#F05454', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Invoice Generated', 'Invoice Generated', '#76E8E0', @Currentdate, @UserId,@CompanyId)
            )
            AS Source ([Id], [StatusName], [InvoiceStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId],[CompanyId])
            ON Target.[StatusName] = Source.[StatusName]  AND Target.CompanyId=Source.CompanyId
            WHEN MATCHED THEN 
            UPDATE SET [StatusName] = Source.[StatusName],
                       [StatusColor] = Source.[StatusColor],
                       [CompanyId] = Source.[CompanyId],
                       [CreatedDateTime] = Source.[CreatedDateTime],
                       [CreatedByUserId] = Source.[CreatedByUserId]
            WHEN NOT MATCHED BY TARGET THEN 
            INSERT ([Id], [StatusName],[InvoiceStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]) 
            VALUES ([Id], [StatusName],[InvoiceStatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]);
            
    MERGE INTO [dbo].[InvoiceQueueTradingStatus] AS Target 
            USING ( VALUES
		        (NEWID(), 'Created', '#00B4D8', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'In Progress', '#2666CF', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Approved', '#06FF00', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Rejected', '#F05454', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Paid', '#009DAE', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Disputed', '#FF0000', @Currentdate, @UserId,@CompanyId)
		        ,(NEWID(), 'Received', '#00EAD3', @Currentdate, @UserId,@CompanyId)
            )
            AS Source ([Id], [StatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId],[CompanyId])
            ON Target.[StatusName] = Source.[StatusName]  AND Target.CompanyId=Source.CompanyId
            WHEN MATCHED THEN 
            UPDATE SET [StatusName] = Source.[StatusName],
                       [StatusColor] = Source.[StatusColor],
                       [CompanyId] = Source.[CompanyId],
                       [CreatedDateTime] = Source.[CreatedDateTime],
                       [CreatedByUserId] = Source.[CreatedByUserId]
            WHEN NOT MATCHED BY TARGET THEN 
            INSERT ([Id], [StatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]) 
            VALUES ([Id], [StatusName], [StatusColor], [CreatedDateTime], [CreatedByUserId], [CompanyId]);
  
	DECLARE @Template TABLE
	(
		EmailTemplateName NVARCHAR(250)
		,[EmailTemplate] NVARCHAR(MAX)
		,[EmailSubject] NVARCHAR(2000)
        ,[EmailTemplateReferenceId] UNIQUEIDENTIFIER
	)
	INSERT INTO @Template(EmailTemplateName,EmailTemplate,EmailSubject,EmailTemplateReferenceId)
	VALUES('SellerSignatureVerificationMailToSgTraderTemplate','<!doctype html>
